// <autogenerated />
using MDD4All.DevOpsObserver.DataModels;
using MDD4All.DevOpsObserver.ViewModels;
using Microsoft.AspNetCore.Components;

namespace MDD4All.DevOpsObserver.Views
{
    public partial class TrafficLight
    {
        [Parameter]
        public StatusViewModel DataContext { get; set; }

        public bool RedOn 
        { 
            get
            {
                bool result = false;

                if (DataContext.Status.Status == DevOpsStatus.Fail || DataContext.Status.Status == DevOpsStatus.Error)
                {
                    result = true;
                }

                return result;
            }
        }

        public bool AmberOn { get
            {
                bool result = false;

                if (DataContext.Status.Status == DevOpsStatus.Warning)
                {
                    result = true;
                }

                return result;
            }
        }
        
        public bool GreenOn 
        {
            get
            {
                bool result = false;

                if (DataContext.Status.Status == DevOpsStatus.Success || DataContext.Status.Status == DevOpsStatus.Unknown)
                {
                    result = true;
                }

                return result;
            }
        }


    }
}